package CaseStudy5;

public class FindLargerNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10;
		int b=20;
		int c=15;
		
		if(a>b && a>c) {
			System.out.println("A is big number");
		}else if(b>a && b>c) {
			System.out.println("B is big number");
		}else if(c>a && c>b) {
			System.out.println("C is big number");
		}

	}

}
